import React from 'react';
import "../assets/css/News.css";
import { Container, Row, Col } from 'react-bootstrap';

export default function Cards(props) {
    const { img, title, txt} = props;
  return (
    <div className='cards'>
        <Container>
          <Row>
            <Col xs={12} className='box'>
              <div  className='principal-new' style={{backgroundImage : 'url(' + {img} + ') '}}>
                <img src={img} alt=''></img>
                <h2 className='new-title'>{title}</h2>
              </div>
              <div>
                <p className='new-txt'>{txt}</p>
              </div>
            </Col>
          </Row>
        </Container>
    </div>
  )
}
